INSERT INTO `products` VALUES (2,'VIỆT QUẤT 125G','Quả việt quất là loại quả được yêu thích trên toàn thế giới không chỉ vì ngon mà còn vì loại quả này cung cấp nhiều chất dinh dưỡng cho cơ thể. Quả việt quất có thể ăn tươi, hay dùng để làm mứt, nghiền ăn kèm với kem, nước quả ép hay bánh hấp.',13990000,1,'https://product.hstatic.net/1000282430/product/viet-quat_large.jpg'),(3,'ỔI PHÚC LỘC THỌ 500G','Không có mô tả',2995000,1,'https://product.hstatic.net/1000282430/product/oi-phuc-loc-tho_9a1f6289626b43a88da39041dd9977c3_large.jpg'),(4,'CHUỐI FOHLA 1KG','Chuối Fohla giống chuối Nam Mỹ được trồng ở Đức Huệ - Long An',5150000,1,'https://product.hstatic.net/1000282430/product/chuoi-fohla_large.jpg'),(5,'CAM NAVEL 500G','Cam Navel lớp vỏ mỏng, mịm, thịt màu vàng, mùi thơm đặc trưng, rất ngọt, không hạt, nhiều nước, thường được ăn tươi và ép nước, làm nước sinh tố…',4495000,1,'https://product.hstatic.net/1000282430/product/cam-navel_large.jpg'),(6,'DÂU TÂY GIỐNG NHẬT FAMILY 250G','Không có mô tả',15490000,1,'https://product.hstatic.net/1000282430/product/dau-family_c344c2353a724bcd85b0c69e954fb628_large.jpg'),(7,'TÁO JULIET HỮU CƠ 500GR','Không có mô tả',5245000,1,'https://product.hstatic.net/1000282430/product/organic-juliet-apple_e9293308ccf34447aba7b5f9d9057dac_large.jpg'),(8,'NHO XANH KHÔNG HẠT 500G','Nho xanh không hạt  được trồng tại nơi có khí hậu ôn đới nơi không khí khô và nhiều nắng. Bởi như thế mới không bị sâu bệnh tấn công và tạo điều kiện phát triển ra quả nho ngon nhất.',19995000,1,'https://product.hstatic.net/1000282430/product/nho-xanh6000200557738_c40c59da9872452593b1426992e9ff91_large.jpg'),(9,'QUÝT 500G','Trái Quýt được tuyển chọn kỹ, quýt có vị ngọt thanh, vỏ căng bóng, đẹp và tươi.',5995000,1,'https://product.hstatic.net/1000282430/product/quyt_large.jpg'),(10,'XOÀI CÁT HÒA LỘC 500G','Xoài Hòa Lộc là giống xoài truyền thống ở Nam Bộ biết bao đời nay, có thể nói nó thuần chủng 100%. Khi quả chín đúng chuẩn sẽ lan toả hương thơm nồng nàn, thịt quả màu